package com.brothergeol.servlet.web.frontcontroller.v5;import com.brothergeol.servlet.web.frontcontroller.v5.adapter.ControllerV2HandlerAdapter;import com.brothergeol.servlet.web.frontcontroller.v5.adapter.ControllerV3HandlerAdapter;import com.brothergeol.servlet.web.frontcontroller.v5.adapter.ControllerV4HandlerAdapter;import java.util.ArrayList;import java.util.List;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class HandlerAdapterConfig {    @Bean    public HandlerAdapterList handlerAdapterList() {        return new HandlerAdapterList(registerHandlerAdapters());    }    private List<MyHandlerAdapter> registerHandlerAdapters() {        List<MyHandlerAdapter> handlerAdapters = new ArrayList<>();        handlerAdapters.add(new ControllerV3HandlerAdapter());        handlerAdapters.add(new ControllerV4HandlerAdapter());        handlerAdapters.add(new ControllerV2HandlerAdapter());        return handlerAdapters;    }}